import React, {useState} from 'react'
import Axios from 'axios';
import '../style/Barang.scss'

const Barang = () => {
    const [id,setID] = useState('');
    const [nama,setNama] = useState('');
    const [harga,setHarga] = useState('');
    const [stok,setStok] = useState('');

    const [qty,setQty] = useState(0);
    const [stokBaru,setStokBaru] = useState(stok);
    const [hargaBaru, setHargaBaru] = useState(harga);
    const [total,setTotal] = useState(0);

    const [barang,setBarang] = useState([]);

    //update ID value
    const updateID = e => {
        setID(e.target.value);
    }

    //update Nama value
    const updateNama = e => {
        setNama(e.target.value);
    }

    //Update Harga value
    const updateHarga = e => {
        setHarga(e.target.value);
    }

    //Update Stok value
    const updateStok = e => {
        setStok(e.target.value);
    }

    //Kurangi stok based on qty for counter button
    const kurangiStok =() => {
       if(stokBaru == 0 ){
            setQty(qty + 0)
            alert ("Stok Kurang")
        } else { 
            setQty(qty+1)
            setStokBaru(stokBaru - 1)
        }
    }

    //Tambah stok based on qty for counter button
    const tambahStok =() => {
        setQty(qty-1)
        setStokBaru(stokBaru +1)
    }

    //Tambah stok based on qty for stok +  button
    const addStok =() => {
        setQty(qty-1)
        setStokBaru(stokBaru +1)
    }

    //Kurangi stok based on qty for stok -  button
    const minusStok =() => {
        setQty(qty+1)
        setStokBaru(stokBaru -1)
    }

    //Update Barang to DB based on new value
    const updateBarang = (id) => {
        alert(id)
        Axios.put("http://localhost:3001/update", { harga: hargaBaru, stok: stokBaru , qty: qty , id: id }).then(
          (response) => {
            setBarang(barang.map((barang) => {
                return barang.id == id ? {id: id,nama: nama, harga: hargaBaru, stok:stokBaru, qty: qty } : barang
            }))
          }
        );
    };

    //Add to Cart
    const addCart = (id) => {
        alert(id)
        Axios.post("http://localhost:3001/createCart",
        {
            id: id,
            nama: nama,
            harga: hargaBaru,
            stok: stokBaru,
            qty: 0,
            total : harga * qty,
        }).then(() => {
        alert("Good");
    });
    }

    //Get Barang from database
    const getBarang = () => {
        Axios.get("http://localhost:3001/barang").then((response) => {
            setBarang(response.data)
        });
    };

    //Delete data from barang based on ID
    const deleteBarang = (id) => {
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
          setBarang(
            barang.filter((barang) => {
              return barang.id != id;
            })
          );
          alert(id)
        });
      };

    return (
        <div>
            <div>
                <span>
                <button onClick={getBarang} className="button__show">Show Barang</button>
                </span>
            </div>
            {barang.map(barang => (
                <div className="content">
            <div className="wrapper">
                <table className="table">
                    <tbody>
                        <td>{barang.id}</td>
                        <td>{barang.nama}</td>
                        <td><input type="number" className="input" value={harga} onChange={updateHarga}></input></td> 
                        <td><button onClick={addStok} className="button__tambah">+</button>{stok}<button onClick={minusStok} className="button__kurang">-</button></td>
                        <td><button onClick={kurangiStok} className="button__tambahQty">+</button>{qty}<button onClick={tambahStok} className="button__kurangQty">-</button></td>
                        <td><button onClick={() => {updateBarang(id);}} className="button__editData"> Update </button></td>
                        <td><button onClick={() => {addCart(id);}} className="button__add"> Add </button></td>
                        <td><button onClick={() => {deleteBarang(barang.id);}}className="button__delete"> Remove </button></td>
                    </tbody>
                </table>
            </div>
        </div>
            ))}

        </div>
    )
}

export default Barang
