export default (state,action) => {
    switch(action.type) {
        case 'DELETE_BARANG':
            return {
                ...state,
                barang: state.barang.filter(barang => barang.id !== action.payload)
            }
        case 'TAMBAH_BARANG':
            return {
                ...state,
                barang : [action.payload, ...state.barang]
            }

    default :
        return state
    }

}
