import React, { useContext } from 'react'
import {BarangContext} from './context/BarangContext'
import { CartProvider } from './context/CartContext';

import NavHeader from './NavHeader';
import Barang from './Barang';
import AddBarang from './AddBarang';
import ListCart from './Cart';


const ListBarang = () => {
    const [barang,setBarang]=useContext(BarangContext);

    const removeBarang = id => {
        const removeBarang = [...barang].filter(b => b.id !== id)
        setBarang(removeBarang)
    }

    return (
        <div>
            <NavHeader />
            <AddBarang />
            {barang.map((barang,index) => (
                <Barang key = {index}
                        id={barang.id}
                        tglMasuk = {barang.tglMasuk}
                        tglKeluar={barang.tglKeluar}
                        nama={barang.nama} 
                        harga={barang.harga}
                        stok={barang.stok}
                        counter={barang.count}
                />
            ))}
        </div>
    )
}

export default ListBarang
