import React, {useState,useContext} from 'react'
import { BarangContext } from './context/BarangContext';
import { CartContext } from './context/CartContext';
import './style/Barang.scss'

const Barang = ({id,tglMasuk,tglKeluar,nama, harga,stok}) => {
    const [qty,setQty] = useState(0);
    const [stokBaru,setStok] = useState(stok);
    const [hargaBaru, setHarga] = useState(harga);
    const [counter,setCounter] = useState(0);

    const [cart,setCart] = useState([]);

    const [barang,setBarang] = useState([])

    const addCart = e => {
        setCart(current => [...current, nama,qty])
        alert (cart)
    }

    const kurangiStok =() => {
       if(stokBaru == 0 ){
            setQty(qty + 0)
            alert ("Stok Kurang")
        } else { 
            setQty(qty+1)
            setStok(stokBaru - 1)
        }
    }

    const tambahStok =() => {
        setQty(qty-1)
        setStok(stokBaru +1)
    }

    const addStok =() => {
        setCounter(counter-1)
        setStok(stokBaru +1)
    }

    const minusStok =() => {
        setCounter(counter+1)
        setStok(stokBaru -1)
    }

    const updateHarga = (e) => {
        setHarga(e.target.value);
    }

    const deleteEmployee = (id) => {
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
          setBarang(
            barang.filter((val) => {
              return val.id != id;
            })
          );
        });
      };

    return (
        <div className="container">
            <div>
                <table className="table">
                    <tbody>
                        <td>{id}</td>
                        <td>{tglMasuk}</td>
                        <td>{tglKeluar}</td>
                        <td>{nama}</td>
                        <td><input type="number" className="input" value={hargaBaru} onChange={updateHarga}></input></td> 
                        <td><button onClick={addStok} className="button__tambah">+</button>{stokBaru}<button onClick={minusStok} className="button__kurang">-</button></td>
                        <td><button onClick={kurangiStok} className="button__tambahQty">+</button>{qty}<button onClick={tambahStok} className="button__kurangQty">-</button></td>
                        <td><button onClick={ deleteEmployee(barang.id)} className="button__delete">Del</button></td>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Barang
