import React, { useState } from 'react';
import Axios from 'axios';
import '../style/SignIn.scss'

const User = () => {
    const [user,setUser]=useState([]);

    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');

    //update Email value
    const updateEmail = e => {
        setEmail(e.target.value);
    }

    //Update Password value
    const updatePassword = e => {
        setPassword(e.target.value);
    }

    //Sign Up
    const signUp = () => {
        alert(noHP)
        Axios.post("http://localhost:3001/createUser", {
          email: email,
          password: password,
        }).then(() => {
          setUser([
            ...user,
            {
                email: email,
                password: password,
            },
          ]);
        });
      };

    return (
        <div>
        <form className="form" onSubmit={signUp}>
            <h1>Sign Up</h1>
            <div>
                <label>Email<input type="text"  className="inputEmail" value={email} onChange={updateEmail}/></label>
            </div>
            <div>
                <label>Password<input type="text"  className="inputPassword" value={password} onChange={updatePassword}/></label><br></br>
            </div>
            <div className="btn__submit">
                <input type="submit" value="Sign In" className="btn" onClick={signUp}/>
            </div>
        </form>
        </div>
    )
}

export default User
