import React, {useState, createContext, useReducer} from 'react'
import BarangReducer from'./BarangReducer'

const initialState = {
  barang: [
  { 
    id: 1001,
    nama: 'Tepung Kanji',
    harga: 50000,
    stok: 10,
    count: 0,
    tglMasuk: '10/01/2021',
    tglKeluar: '15/01/2021',
  },
  { 
    id: 1002,
    nama: 'Tepung Maizena',
    harga: 50000,
    stok: 3,
    count: 0,
    tglMasuk: '10/01/2021',
    tglKeluar: '15/01/2021',
  },
]};


export const BarangContext = createContext(initialState);

export const  BarangProvider = props => {

    return (
        <BarangContext.Provider value={{
          barang: state.barang,}

          }>
            {props.children}
        </BarangContext.Provider>
    );
};
