import React, {useState} from 'react'
import './style/Barang.scss'

const Barang = ({id,tglMasuk,tglKeluar,nama, harga,stok}) => {
    const [count,setCount] = useState(0);
    const [stokBaru,setStok] = useState(stok);
    const [counter,setCounter] = useState(0);
    const [total, setTotal] = useState(0);

    const kurangiStok =() => {
       if(stokBaru == 0 ){
            setCount(count+ 0)
            alert ("Stok Kurang")
        } else { 
            setCount(count+1)
            setStok(stokBaru - 1)
        }
    }

    const tambahStok =() => {
        setCount(count-1)
        setStok(stokBaru +1)
    }

    const addStok =() => {
        setCounter(counter-1)
        setStok(stokBaru +1)
    }

    const minusStok =() => {
        setCounter(counter+1)
        setStok(stokBaru -1)
    }

    const countTotal = () => {
        setTotal(count * harga)
    }

    return (
        <div className="container">
            <div>
                <div className="wrapper__total">
                    <p></p>
                    <p>{total}</p>
                </div>
                <table className="table">
                    <tbody>
                        <td>{id}</td>
                        <td>{tglMasuk}</td>
                        <td>{tglKeluar}</td>
                        <td>{nama}</td>
                        <td>{harga}</td>
                        <td><button onClick={addStok} className="button__tambah">+</button>{stokBaru}<button onClick={minusStok} className="button__kurang">-</button></td>
                        <td><button onClick={kurangiStok} className="button__tambah">Beli</button>{count}<button onClick={tambahStok} className="button__kurang">Jual</button></td>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Barang
